INSERT IGNORE INTO ISHotelCampus.servizio (id, nome, descrizione, costo, immagine, is_deleted) VALUES (1,'Colazione', 'Offriamo un servizio di colazione a buffet che combina la tradizione della colazione continentale con l''autenticità dei prodotti locali. Ogni mattina, gli ospiti possono gustare un''ampia selezione di pane fresco, croissant, salumi, formaggi, frutta di stagione e altre specialità locali. Inizia la giornata con energia grazie alla nostra colazione, pensata per soddisfare tutti i palati e offrire un assaggio del territorio. Orario colazione: 7:00 - 10:00', 10, 'Images/Demo/Servizi/colazione1.jpg', false);
INSERT IGNORE INTO ISHotelCampus.servizio (id, nome, descrizione, costo, immagine, is_deleted) VALUES (2,'Ristorante', 'Il ristorante del nostro hotel offre un''esperienza culinaria di alta qualità, combinando piatti raffinati e ingredienti freschi in un''atmosfera accogliente e familiare. Perfetto per gli ospiti che desiderano una cena deliziosa senza lasciare l''hotel, il nostro ristorante garantisce un''esperienza gastronomica memorabile per ogni occasione, rendendo il soggiorno ancora più piacevole. Orari ristorante: 12:00 - 15:00 / 19:00 - 22:00', 30, 'Images/Demo/Servizi/ristorante1.jpg', false);
INSERT IGNORE INTO ISHotelCampus.servizio (id, nome, descrizione, costo, immagine, is_deleted) VALUES (3,'Piscina', 'Concediti un momento di puro relax nella nostra piscina coperta. Immersa in un''atmosfera serena e raffinata, la nostra piscina interna ti offre l''opportunità di rilassarti in qualsiasi momento dell''anno. Goditi un momento di tranquillità in un ambiente privato, ideale per liberare la mente e rigenerare il corpo. Che tu voglia rilassarti dopo una giornata intensa o iniziare la mattina con una nuotata rigenerante, la nostra piscina coperta è il luogo ideale per ritrovare equilibrio e benessere. Orari piscina: 9:00 - 22:00 (Lun - Ven) 11:00 - 00:00 (Sab - Dom)', 20, 'Images/Demo/Servizi/piscinaInterna1.jpg', false);
INSERT IGNORE INTO ISHotelCampus.servizio (id, nome, descrizione, costo, immagine, is_deleted) VALUES (4,'Spa e centro benessere', 'Immergiti in un''oasi di relax nel nostro esclusivo centro benessere. Goditi un percorso termale completo con saune, bagni turchi e vasche idromassaggio. Approfitta del nostro trattamento esclusivo: un massaggio personalizzato di 45 minuti per rilassare i muscoli e rivitalizzare i sensi. Lasciati coccolare dai nostri esperti terapisti per un''esperienza indimenticabile. Orari centro benessere: 15:00 - 21:00', 40, 'Images/Demo/Servizi/spa1.jpg', false);
